update AD_WINDOW_ACCESS set AD_CLIENT_ID = (Select AD_CLIENT_ID from AD_Role where AD_Role.AD_Role_ID = AD_WINDOW_ACCESS.AD_Role_ID) where AD_CLIENT_ID = 0 and AD_Role_ID<>0;